	// Your code goes here
});

// If the command is not VIP only we can allow this command to execute
if (!command.vipOnly) return false;

// S
const userIsVIP = await isUserVIP(message.author.id)
// The user is VIP so we can allow this command to be used
if (userIsVIP) return false;

// The user is NOT VIP so we throw an error
throw `The ${command.name} is only allowed for VIP users.`

